import React from 'react';
import { ExamplePageProps } from './ExamplePage.propTypes'
import { ReactRouterDetails } from '../../components/ReactRouterDetails/ReactRouterDetails.component';
import styles from './ExamplePage.styles.scss';
import tripAPI from "../../utils/tripAPI";

export class ExamplePage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      trips: []
    };
  }

  componentDidMount() {
    // console.log('componentDidMount()', this.props);
    tripAPI.getTrips().then(res => {
      var data = res.data.trips[0];
      console.log("RESPONSE.DATA.TRIPS", data);
      this.setState({
        trips: {
          title: data.title,
          location: data.location,
          description: data.description,
          date: data.date,
          image: data.image
        }
      });
    })
    .catch(err => {
      console.log("ERROR, could not load trips", err);
    })
  }

  componentDidUpdate(prevProps, prevState, prevSnapshot) {
    console.log('componentDidUpdate(prevProps, prevState, prevSnapshot)', prevProps, prevState, prevSnapshot);
  }


  render() {
    return (
      <div>
        <h1>{this.state.trips.title}</h1>
        <h2>Location: {this.state.trips.location}</h2>
        <p>Description: {this.state.trips.description}</p>
        <p>Date: {this.state.trips.date}</p>
        <img src={this.state.trips.image} />
      </div>
    );
  }
}

ExamplePage.propTypes = ExamplePageProps.propTypes;
ExamplePage.defaultProps = ExamplePageProps.defaultProps;
