import React from 'react';
import { ExampleFormProps } from './ExampleForm.propTypes';
import { Button, Form, FormGroup, Label, Input, FormText } from 'reactstrap';


function ExampleForm(props) {

    return (
        <Form>
        <FormGroup>
          <Label for="exampleEmail">Title</Label>
          <Input type="textarea" name="title" id="exampleEmail" placeholder="with a placeholder" 
           value={ title }
           onChange={ (e) => {
                       this.handleChange(e)
                     } }/>
        </FormGroup>
        <FormGroup>
          <Label for="examplePassword">Location</Label>
          <Input type="textarea" name="Location" id="examplePassword" placeholder="password placeholder" />
        </FormGroup>
        <FormGroup>
          <Label for="examplePassword">Date</Label>
          <Input type="textarea" name="date" id="examplePassword" placeholder="password placeholder" />
        </FormGroup>
        <FormGroup>
          <Label for="exampleText">Description</Label>
          <Input type="textarea" name="Description" id="exampleText" />
        </FormGroup>
        <FormGroup>
          <Label for="exampleFile">Image</Label>
          <Input type="file" name="file" id="exampleFile" />
          <FormText color="muted">
            This is some placeholder block-level help text for the above input.
            It's a bit lighter and easily wraps to a new line.
          </FormText>
        </FormGroup>
        <Button>Submit</Button>
      </Form>
    );
};

ExampleForm.propTypes = ExampleFormProps.propTypes;
ExampleForm.defaultProps = ExampleFormProps.defaultProps;

export default ExampleForm;
