  render() {
    return (
      <Container>
        <Form onSubmit={this.handleSubmit}>
          <FormGroup>
            <Label htmlFor="title">title</Label>
            <Input
              type="text"
              name="title"
              id="title"
              placeholder=""
            />
          </FormGroup>
          <FormGroup>
            <Label htmlFor="location">location</Label>
            <Input
              type="text"
              name="location"
              id="location"
              placeholder=""
            />
          </FormGroup>
          <FormGroup>
            <Label htmlFor="Description">Description</Label>
            <Input
              type="text"
              name="Description"
              id="Description"
              placeholder=""
            />
          </FormGroup>
          <FormGroup>
            <Label htmlFor="tripDate">Trip Date</Label>
            <Input
              type="text"
              name="tripDate"
              id="tripDate"
              placeholder="2022-12-12 00:00:00"
            />
          </FormGroup>

          <Button>Submit</Button>
        </Form>
      </Container>
    );
  }
}
