import React from 'react';
import { ExamplePageProps } from './ExamplePage.propTypes'
import { ReactRouterDetails } from '../../components/ReactRouterDetails/ReactRouterDetails.component';
import styles from './ExamplePage.styles.scss';
import tripAPI from "../../utils/tripAPI";
import { TripCard } from '../../components/ExampE/ExampE.component';
import {
  Card, CardImg, CardText, CardBody,
  CardTitle, CardSubtitle, Button
} from 'reactstrap';

export class ExamplePage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      trips: []
    };
  }

  componentDidMount() {
    this.loadTrips();
  }

  loadTrips = () => {
    tripAPI.getTrips()
      .then(res => this.setState({ trips: res.data }))
      .catch(err => console.log(err));
  };

  componentDidUpdate(prevProps, prevState, prevSnapshot) {
    // console.log('componentDidUpdate(prevProps, prevState, prevSnapshot)', prevProps, prevState, prevSnapshot);
  }


  render() {
    return (
      <div>
        {this.state.trips.map(trip => (
      <Card>
          <CardImg top width="100%" src={trip.image} alt="Card image cap" />
          <CardBody>
            <CardTitle>Title: {trip.title}</CardTitle>
            <CardSubtitle>Location: {trip.location}</CardSubtitle>
            <CardText>Description:{trip.description}</CardText>
            <CardText>Date:{trip.date}</CardText>
          </CardBody>
        </Card>
        ))}
        </div>
    );
  }
}

ExamplePage.propTypes = ExamplePageProps.propTypes;
ExamplePage.defaultProps = ExamplePageProps.defaultProps;
