import React from 'react';
import { TripCardProps } from './TripCard.propTypes';
import styles from './TripCard.styles.scss';

export const TripCard = props => {
    return (
        <div>
          <Card body>
              <CardTitle>Trip: {props.tripName}</CardTitle>
              <CardText>
                <img style={{height:"200px", width:"250px"}} src={props.image} />
              </CardText>
              <Button>Show More Info</Button>
            </Card>
        </div>
    );
};

TripCard.propTypes = TripCardProps.propTypes;
TripCard.defaultProps = TripCardProps.defaultProps;
