import Jumbotron from "../../components/MyTrip/MyTrip";
import { Container, Row, Col, Button } from "reactstrap";
import MyTrip from "../../components/MyTrip/MyTrip";
import tripAPI from '../../utils/tripAPI';
import activitiesAPI from "../../utils/activityAPI";


export class MyTripPagePage extends React.Component {

      state = {
        trips: [],
        activities: []
    };

  componentDidMount() {
    // this.getTrips();
    this.getActivities();
  }


  getTrips = () => {
    tripAPI.getTrips()
      .then(res => {

        // var tripArray = res.data.trips
        console.log("RESPONSE", res.data.trips);
        this.setState({ trips: res.data.trips })
        console.log("THIS.STATE.TRIPS", this.state.trips[0].title);

      })
      .catch(err => console.log(err));

  };

getActivities = () => {
  tripAPI.getActivities()
  .then(res => {
    console.log("RESPONSE FOR ACTIVITIES", res)
    // this.setState({activities:})
  })
  .catch(err => console.log(err));
}



  render() {
    return (
      <div>
     {this.state.trips.map(trip => (
        <MyTrip
          //passing trip props
          tripName={trip.title}
          image={trip.image}
          destination={trip.location}
          date={trip.tripDate}
          description={trip.Description}

          //passing activitiy props

        />
      ))} 


      {/* <MyTrip onClick={this.handleTrips} tripName={this.state.trips.map(trip => {return trip.title})} image={this.state.trips.map(trip=>{return trip.image})} /> */}

    </div>
    );
  }
}

