import React from 'react';
// import { ReactRouterDetails } from '../../components/ReactRouterDetails/ReactRouterDetails.component';
// import styles from './TripPage.styles.scss';
// import Trip from '../../components/Trip/Trip'
import { Form, FormGroup, Label, Input, FormText } from 'reactstrap';
import { Container, Row, Col } from 'reactstrap';
import { Jumbotron, Button } from 'reactstrap';
import tripAPI from '../../utils/tripAPI';
import axios from 'axios';

export default class TripPage extends React.Component {
  constructor(props) {
    super(props);
    this.handleSubmit = this.handleSubmit.bind(this);
  }
  componentDidMount() {
    // console.log('componentDidMount()', this.props);
  }
  componentDidUpdate(prevProps, prevState, prevSnapshot) {
    console.log('componentDidUpdate(prevProps, prevState, prevSnapshot)', prevProps, prevState, prevSnapshot);
  }
  handleSubmit(event) {
    event.preventDefault()
    // const data = new FormData(event.target)
    // console.log("THIS IS THE DATA", event.target);

  const testData = {
      "title": "Fishing trip",
      "location": "Florida Keys",
      "Description": "I want to catch a shark!",
      "tripDate": "2022-12-12",
      "image": "https://www.usnews.com/dims4/USNEWS/51a07af/2147483647/resize/1200x%3E/quality/85/?url=http%3A%2F%2Fcom-usnews-beam-media.s3.amazonaws.com%2F47%2Fcb%2F12d46a8a47fb9616e593f5667cf4%2F1.%20Florida%20Keys%20Getty.jpg"
      }

    fetch('/api/trips', {
      method: 'POST',
      body: testData
    });
  };

  render() {
    return (
      <Container>
        <Form onSubmit={this.handleSubmit}>
          <FormGroup>
            <Label htmlFor="title">title</Label>
            <Input
              type="text"
              name="title"
              id="title"
              placeholder=""
            />
          </FormGroup>
          <FormGroup>
            <Label htmlFor="location">location</Label>
            <Input
              type="text"
              name="location"
              id="location"
              placeholder=""
            />
          </FormGroup>
          <FormGroup>
            <Label htmlFor="Description">Description</Label>
            <Input
              type="text"
              name="Description"
              id="Description"
              placeholder=""
            />
          </FormGroup>
          <FormGroup>
            <Label htmlFor="tripDate">Trip Date</Label>
            <Input
              type="text"
              name="tripDate"
              id="tripDate"
              placeholder="2022-12-12 00:00:00"
            />
          </FormGroup>

          <Button>Submit</Button>
        </Form>
      </Container>
    );
  }
}
