import React from 'react';
import { TripPageProps } from './TripPage.propTypes'
import { ReactRouterDetails } from '../../components/ReactRouterDetails/ReactRouterDetails.component';
import styles from './TripPage.styles.scss';
import Trip from '../../components/Trip/Trip'

import { Form, FormGroup, Label, Input, FormText } from 'reactstrap';
import { Container, Row, Col } from 'reactstrap';
import { Jumbotron, Button } from 'reactstrap';
import { Card, CardTitle, CardText, CardImg, CardImgOverlay } from 'reactstrap';
import { Media } from 'reactstrap';



export class TripPage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      title: '',
      location: '',
      Description: '',
      tripDate: '',
      image: ''
    }
    this.handleSubmit = this.handleSubmit.bind(this)
  }

  componentDidMount() {
    console.log('componentDidMount()', this.props);

    }
  componentDidUpdate(prevProps, prevState, prevSnapshot) {
    console.log('componentDidUpdate(prevProps, prevState, prevSnapshot)', prevProps, prevState, prevSnapshot);
  }

  render() {
    return (
      <div>
            <Container>
        <Row>
          <Col xs="5" sm="7">
            <FormGroup>
            <div>
            <Jumbotron Title>
          <Container fluid>
          <Media>
            <Media left href="#">
              <Media object data-src="holder.js/64x64" alt="Generic placeholder image" />
            </Media>
            <Label for="exampleText"> Title</Label>
          <Input type="textarea" name="text" id="exampleText" />
          </Media>
          </Container>
          </Jumbotron>
          </div>
          {/* ========================================== */}
          <div>
          <Jumbotron AddDestination>
          <Container fluid>
          <Media>
            <Media left href="#">
              <Media object data-src="holder.js/64x64" alt="Generic placeholder image" />
            </Media>
          <Label for="exampleText"> Add Destination</Label>
          <Input type="textarea" name="text" id="exampleText" />
          </Media>
          </Container>
          </Jumbotron>
            </div>
            {/* ================================= */}
          <Jumbotron DateTime>
          <Container fluid>
          <Media>
            <Media left href="#">
              <Media object data-src="holder.js/64x64" alt="Generic placeholder image" />
            </Media>
          <Label for="exampleText"> Date Time</Label>
          <Input type="textarea" name="text" id="exampleText" />
          </Media>
          </Container>
          </Jumbotron>
          </FormGroup>
          </Col>
          {/* ==================================== */}
          <Col xs="6" sm="5">
            <FormGroup>
            <div>
        <Card body>
          <CardTitle>Write Invite</CardTitle>
          <Input type="textarea" name="text" id="exampleText" />

          <Button>Send Invite</Button>
        </Card>

            <Button color="primary" size="lg" active>Share</Button>{' '}
            <Button color="primary" size="lg" active>Submit</Button>{' '}

             </div>
            </FormGroup>

          </Col>
        </Row>
      </Container>
      </div>
    );
  }
}

TripPage.propTypes = TripPageProps.propTypes;
TripPage.defaultProps = TripPageProps.defaultProps;
